export const initialState = {
  mainPosts: [
    {
      id: 1,
      User: { id: 1, nickname: "티라노" },
      content: "첫글임",
      Images: [
        {
          src:
            "https://www.google.com/imgres?imgurl=https%3A%2F%2Fstatic.toiimg.com%2Fphoto%2F72975551.cms&imgrefurl=https%3A%2F%2Ftimesofindia.indiatimes.com%2Flife-style%2Fevents%2Fsurya-grahan-today-annular-solar-eclipse-2019-december-images-photos-pics-video-check-out-these-breathing-pictures-of-the-seasons-last-surya-grahan-of-26-december-2019-india%2Fphotostory%2F72975550.cms&tbnid=pTd2OIa33-6LVM&vet=12ahUKEwjwjJHXjYXvAhUYBN4KHVEyA3QQMygEegUIARCwAQ..i&docid=78aIM0GrmvG9rM&w=1200&h=900&q=images&hl=ko&ved=2ahUKEwjwjJHXjYXvAhUYBN4KHVEyA3QQMygEegUIARCwAQ",
        },
        {
          src:
            "https://www.google.com/imgres?imgurl=https%3A%2F%2Fimages.unsplash.com%2Fphoto-1494548162494-384bba4ab999%3Fixid%3DMXwxMjA3fDB8MHxzZWFyY2h8MXx8c3VucmlzZXxlbnwwfHwwfA%253D%253D%26ixlib%3Drb-1.2.1%26w%3D1000%26q%3D80&imgrefurl=https%3A%2F%2Funsplash.com%2Fs%2Fphotos%2Fsunrise&tbnid=a9B7raWE3PxoBM&vet=12ahUKEwjwjJHXjYXvAhUYBN4KHVEyA3QQMygCegUIARCsAQ..i&docid=HARoYW82hsGckM&w=1000&h=1000&q=images&hl=ko&ved=2ahUKEwjwjJHXjYXvAhUYBN4KHVEyA3QQMygCegUIARCsAQ",
        },
        {
          src:
            "https://www.google.com/imgres?imgurl=https%3A%2F%2Fimages.ctfassets.net%2Fhrltx12pl8hq%2F4plHDVeTkWuFMihxQnzBSb%2Faea2f06d675c3d710d095306e377382f%2Fshutterstock_554314555_copy.jpg&imgrefurl=https%3A%2F%2Fwww.shutterstock.com%2Fdiscover%2F10-free-stock-images&tbnid=1Y5Fex7Bw6VMkM&vet=12ahUKEwjwjJHXjYXvAhUYBN4KHVEyA3QQMygHegUIARC2AQ..i&docid=YAMnwpTKFlPEWM&w=2500&h=1227&q=images&hl=ko&ved=2ahUKEwjwjJHXjYXvAhUYBN4KHVEyA3QQMygHegUIARC2AQ",
        },
      ],
      Comments: [
        {
          User: {
            nickname: "네로",
          },
          content: "개정판나옴",
        },
        {
          User: {
            nickname: "히어로",
          },
          content: "자고싶다.",
        },
      ],
    },
  ],
  imagePaths: [],
  postAdded: false,
};

const ADD_POST = "ADD_POST";
export const addPost = {
  type: ADD_POST,
};
const dummyPost = {
  id: 2,
  content: "더미 데이터임",
  User: {
    id: 1,
    nickname: "티란오",
  },
  Images: [],
  Comments: [],
};
const reducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_POST:
      return {
        ...state,
        mainPosts: [dummyPost, ...state.mainPosts],
      };
    default:
      break;
  }
};

export default reducer;
