import { Button, Form, Input } from "antd";
import Link from "next/Link";
import { useCallback, useState } from "react";
import styled from "styled-components";
import PropTypes from "prop-types";
import useInput from "../hooks/useInput";
// div태그면서 css를 적용시킴
const ButtonWrapper = styled.div`
  margin-top: 10px;
`;

const FormWrapper = styled(Form)`
  padding: 10px;
`;
const LoginForm = ({ setIsLoggedIn }) => {
  const [Id, onChangeId] = useInput("");
  const [Password, onChangePassword] = useInput("");

  // 컴포넌트의 props로 넘겨주는 함수는 useCallback을 쓰면 최적화가 된다
  const onSubmitForm = useCallback(() => {
    console.log(Id, Password);
    setIsLoggedIn(true);
  }, [Id, Password]);
  return (
    // onFinish에는 e.preventDefault가 적용되어있으므로 그 처리를 안해도된다.
    <FormWrapper onFinish={onSubmitForm}>
      <div>
        <label htmlFor="user-id">아이디</label>
        <br />
        <Input name="user-id" value={Id} onChange={onChangeId} required />
      </div>
      <div>
        <label htmlFor="user-password">비밀번호</label>
        <br />
        <Input
          name="user-password"
          type="password"
          value={Password}
          onChange={onChangePassword}
          required
        />
      </div>
      {/* 
        태그에다가 직접 스타일을 지정하면 
        리액트의 virtualDom이 리렌더링 시켜버리니까 (직접 스타일을 지정한 부분만
        Styled-components를 쓰면된다. 
        
        성능에 크게 문제가 없으면 그냥 인라인으로 써도됨.
        styled-components로 쓰면 최적화할때 좋다.
      */}
      <ButtonWrapper>
        <Button type="primary" htmlType="submit" loading={false}>
          로그인
        </Button>
        {/* href는 Link에 넣는게 좋고, a태그에는 안넣는게 좋다  */}
        <Link href="/signup">
          <a>
            <Button>회원가입</Button>
          </a>
        </Link>
      </ButtonWrapper>
      <div></div>
    </FormWrapper>
  );
};

LoginForm.proTypes = {
  setIsLoggedIn: PropTypes.func.isRequired,
};
export default LoginForm;
