import PropTypes from "prop-types";
const PostCardContent = ({ postData }) => (
  <div>{postData.split(/(#[^\s#]+)/g)}</div>
);

PostCardContent.prototype = { postData: PropTypes.string.isRequired };

export default PostCardContent;
