export const initialState = {
  isLoggedIn: false,
  isLoggingIn: false, // 로그인 시도중 (로딩창 띄우기위함)
  isLoggingOut: false, // 로그아웃 시도중
  me: null,
  signUpData: {},
  loginData: {},
};

export const loginRequestAction = (data) => {
  return {
    type: "LOG_IN_REQUEST",
    data,
  };
};
// loginSuccessAction, loginFailureAction, logoutSuccessAction, logoutFailureAction은 saga에서 호출하므로 안써도됨
export const logoutRequestAction = (data) => {
  return {
    type: "LOG_OUT_REQUEST",
  };
};
const reducer = (state = initialState, action) => {
  switch (action.type) {
    case "LOG_IN_REQUEST":
      return {
        ...state,
        isLoggedIn: true,
      };
    case "LOG_IN_SUCCESS":
      console.log("리듀서 옵니까?");
      return {
        ...state,
        isLoggedIn: true,
        isLoggingIn: false,
        me: { ...action.data, nickname: "nnnnn111" },
      };
    case "LOG_IN_FAILURE":
      return {
        ...state,
        isLoggedIn: false,
        isLoggingIn: false,
      };
    case "LOG_OUT_REQUEST":
      return {
        ...state,
        isLoggingOut: true,
      };
    case "LOG_OUT_SUCCESS":
      return {
        ...state,
        isLoggedIn: false,
        isLoggingOut: false,
        me: null,
      };
    case "LOG_OUT_FAILURE":
      return {
        ...state,
        isLoggingOut: false,
      };
    default:
      return state;
  }
};

export default reducer;
